import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { Tarea } from '../models/tareas';
import { TareaFactoryService } from './tarea-factory.service';


@Injectable({
  providedIn: 'root'
})
export class TareaService {

  URL_API = 'http://localhost:4000/api/tareas';

  tareas! : Tarea[];

  constructor(private http: HttpClient, private tareaFactory: TareaFactoryService) { }

  selectedTarea: Tarea = this.tareaFactory.createTarea(0);
  tareas: Tarea[] = [];

  getTareas(){
    return this.http.get<Tarea[]>(this.URL_API);
  }
  createTarea(tarea: Tarea){
    return this.http.post(this.URL_API, tarea);
  }
  putTarea(tarea: Tarea){
    return this.http.put(`${this.URL_API}/${tarea.idActividad}`, tarea)
  }

  deleteTarea(idActividad: number){
    return this.http.delete(`${this.URL_API}/${idActividad}`)
  }
}
