import { Component } from '@angular/core';
import {TareaService} from '../../services/tarea.service'
import { NgForm } from '@angular/forms'
import { Tarea } from 'src/app/models/tareas';
import { TareaFactoryService } from '../../services/tarea-factory.service';


@Component({
  selector: 'app-tareas',
  templateUrl: './tareas.component.html',
  styleUrls: ['./tareas.component.css']
})
export class TareasComponent {

  constructor(public tareaServices: TareaService, private tareaFactory: TareaFactoryService) {}

  ngOnInit(): void { 
    this.getTareas();
   }
   resetForm(form: NgForm){
    form.reset();
  }

  getTareas() {
    this.tareaServices.getTareas().subscribe(
      res => {
        this.tareaServices.tareas = res;
      },
      err => console.error(err)
    );
  }
addTarea(form: NgForm) {
  if (form.value.idActividad) {
    // Si existe, realiza la actualización (edit)
    this.tareaServices.putTarea(form.value).subscribe(
      res => {
        this.getTareas();
        form.reset();
      },
      err => console.error(err)
    );
  } else {
    // Si no existe, crea una nueva tarea
    this.tareaServices.createTarea(form.value).subscribe(
      res => {
        this.getTareas();
        form.reset();
      },
      err => console.error(err)
    );
  }
}




deleteTarea(id: number) {
  if (confirm('Estás seguro de querer eliminar la tarea?')) {
    this.tareaServices.deleteTarea(id).subscribe(
      () => {
        // Luego de borrar, actualiza la lista de comandas
        this.getTareas();
      },
      (error) => {
        console.error(error);
      }
    );
    }}

    editTarea(tarea: Tarea) {
      this.tareaServices.selectedTarea = tarea;
    }


}
<div class="row">
    <div class="col-md-4">
        <div class="card">
            <div class="card-body">
                <form #tareaForm="ngForm" (ngSubmit)="addTarea(tareaForm)">
                    <input type="hidden" name="idActividad" [(ngModel)]="tareaServices.selectedTarea.idActividad">
                    <div class="form-group">
                        <input type="text" class="form-control" name="datosCompartidos" placeholder="Titulo de tarea" [(ngModel)]="tareaServices.selectedTarea.datosCompartidos" >
                    </div>
                    <br>
                    <div class="form-group">
                        <input type="text" class="form-control" name="tareaDatosEspecifico" placeholder="Informacion sobre la tarea" [(ngModel)]="tareaServices.selectedTarea.tareaDatosEspecifico">
                    </div>
                    <br>
                    <div class="form-group">
                        <input type="date" class="form-control" name="fechaTerminacion" value="2023-12-05" min="2023-12-05" max="2222-12-31" [(ngModel)]="tareaServices.selectedTarea.fechaTerminacion">                      
                    </div>                      
                    <br>
                    <button class="botto btn btn-primary" (click)="resetForm(tareaForm)">Limpiar</button>
                    <button type="submit" class="btn btn-secondary">Guardar</button>
                </form>
            </div>
        </div>
    </div>
    <div class="col-md-8">
        <div class="row">
            <div *ngFor="let tarea of tareaServices.tareas" class="col-md-4">
                <div class="card mb-4">
                    <div class="card-body">
                        <h5 class="card-title">{{tarea.datosCompartidos}}</h5>
                        <p class="card-text">{{tarea.tareaDatosEspecifico}}</p>
                        <p class="card-text"><strong>Fecha de creación:</strong> {{tarea.fechaCreacion | date:"dd-MM-yyyy"}}</p>
                        <p class="card-text"><strong>Fecha límite:</strong> {{tarea.fechaTerminacion}}</p>
                        <button class="btn btn-sm btn-secondary" (click)="editTarea(tarea)"><i class="material-icons">edit</i></button>
                        <button class="btn btn-sm btn-danger" (click)="tarea.idActividad !== undefined ? deleteTarea(tarea.idActividad) : null"><i class="material-icons">delete</i></button>
                    </div>
                </div>
            </div>
        </div>
    </div>
</div>
