import { Component } from '@angular/core';
import { TareaService } from '../../services/tarea.service';
import { NgForm } from '@angular/forms';
import { TareaFactoryService } from 'src/app/services/tarea-factory.service';

@Component({
  selector: 'app-tareas',
  templateUrl: './tareas.component.html',
  styleUrls: ['./tareas.component.css'],
})
export class TareasComponent {
  nombresDeTareas: string[] = [];
  mostrarNuevoTitulo: boolean = false;

  constructor(
    public tareaServices: TareaService,
    private tareaFactoryService: TareaFactoryService
  ) {}

  ngOnInit(): void {
    this.getTareas();
    this.getNombresDeTareas();
  }

  resetForm(form: NgForm) {
    form.reset();
  }

  getTareas() {
    this.tareaServices.getTareas().subscribe(
      (res) => {
        this.tareaServices.tareas = res;
        this.getNombresDeTareas();
      },
      (err) => console.error(err)
    );
  }

  getNombresDeTareas() {
    this.tareaServices.getNombresDeTareas().subscribe(
      (nombres) => (this.nombresDeTareas = nombres),
      (err) => console.error(err)
    );
  }

  addTarea(form: NgForm) {
    const nombreTarea =
      this.mostrarNuevoTitulo && form.value.datosCompartidos
        ? form.value.datosCompartidos
        : form.value.datosCompartidos || '';

    const tarea = this.tareaFactoryService.getTarea(nombreTarea);

    tarea.tareaDatosEspecifico = form.value.tareaDatosEspecifico;
    tarea.fechaTerminacion = form.value.fechaTerminacion;

    if (form.value.idActividad) {
      // Si existe, realiza la actualización (edit)
      this.tareaServices.putTarea(tarea).subscribe(
        (res) => {
          this.getTareas();
          form.reset();
        },
        (err) => console.error(err)
      );
    } else {
      // Si no existe, crea una nueva tarea
      this.tareaServices.createTarea(tarea).subscribe(
        (res) => {
          this.getTareas();
          form.reset();
        },
        (err) => console.error(err)
      );
    }
  }

  deleteTarea(id: number) {
    if (confirm('Estás seguro de querer eliminar la tarea?')) {
      this.tareaServices.deleteTarea(id).subscribe(
        () => {
          // Luego de borrar, actualiza la lista de tareas
          this.getTareas();
        },
        (error) => {
          console.error(error);
        }
      );
    }
  }

  editTarea(tarea: Tarea) {
    this.tareaServices.selectedTarea = tarea;
  }

  toggleMostrarNuevoTitulo() {
    this.mostrarNuevoTitulo = !this.mostrarNuevoTitulo;
    if (!this.mostrarNuevoTitulo) {
      this.tareaServices.selectedTarea.datosCompartidos = '';
    }
  }
}
