import { Component } from '@angular/core';
import {TareaService} from '../../services/tarea.service'
import { NgForm } from '@angular/forms'
import { Tarea } from 'src/app/models/tareas';
import { TareaFactoryService } from '../../services/tarea-factory.service';


@Component({
  selector: 'app-tareas',
  templateUrl: './tareas.component.html',
  styleUrls: ['./tareas.component.css']
})
export class TareasComponent {

  constructor(public tareaServices: TareaService, private tareaFactory: TareaFactoryService) {}

  ngOnInit(): void { 
    this.getTareas();
   }
   resetForm(form: NgForm){
    form.reset();
  }

  getTareas() {
    this.tareaServices.getTareas().subscribe(
      res => {
        this.tareaServices.tareas = res;
      },
      err => console.error(err)
    );
  }
addTarea(form: NgForm) {
  if (form.value.idActividad) {
    // Si existe, realiza la actualización (edit)
    this.tareaServices.putTarea(form.value).subscribe(
      res => {
        this.getTareas();
        form.reset();
      },
      err => console.error(err)
    );
  } else {
    // Si no existe, crea una nueva tarea
    this.tareaServices.createTarea(form.value).subscribe(
      res => {
        this.getTareas();
        form.reset();
      },
      err => console.error(err)
    );
  }
}




deleteTarea(id: number) {
  if (confirm('Estás seguro de querer eliminar la tarea?')) {
    this.tareaServices.deleteTarea(id).subscribe(
      () => {
        // Luego de borrar, actualiza la lista de comandas
        this.getTareas();
      },
      (error) => {
        console.error(error);
      }
    );
    }}

    editTarea(tarea: Tarea) {
      this.tareaServices.selectedTarea = tarea;
    }


}
