import { Injectable } from '@angular/core';
import { Tarea } from '../models/tareas';

@Injectable({
  providedIn: 'root'
})
export class TareaFactoryService {
  // Mapa para almacenar instancias de Tarea por su id
  private tareas: Map<number, Tarea> = new Map<number, Tarea>();

  createTarea(id: number): Tarea {
    if (this.tareas.has(id)) {
      // Si ya existe una instancia con el id, devuelve esa instancia
      return this.tareas.get(id)!;
    } else {
      // Si no existe, crea una nueva instancia de Tarea y la almacena en el mapa
      const nuevaTarea = new Tarea();
      this.tareas.set(id, nuevaTarea);
      return nuevaTarea;
    }
  }
}
