import { Injectable } from '@angular/core';
import {HttpClient} from '@angular/common/http';
import { Tarea } from '../models/tareas';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Injectable({
  providedIn: 'root'
})
export class TareaService {

  URL_API = 'http://localhost:4000/api/tareas';

  selectedTarea : Tarea = {
    datosCompartidos: '',
    tareaDatosEspecifico: '',
    fechaTerminacion: ''
  };
  tareas! : Tarea[];

  constructor(private http: HttpClient) { }

  getNombresDeTareas(): Observable<string[]> {
    return this.getTareas().pipe(
      map(tareas => [''].concat(tareas.map(tarea => tarea.datosCompartidos)))
    );
  }

  getTareas(){
    return this.http.get<Tarea[]>(this.URL_API);
  }
  createTarea(tarea: Tarea){
    return this.http.post(this.URL_API, tarea);
  }
  putTarea(tarea: Tarea){
    return this.http.put(`${this.URL_API}/${tarea.idActividad}`, tarea)
  }

  deleteTarea(idActividad: number){
    return this.http.delete(`${this.URL_API}/${idActividad}`)
  }
}
